import NearestRides from './views/NearestRides';
import UpcomingRides from './views/UpcomingRides';
import PastRides from './views/PastRides';
import React from 'react';
import ReactDOM from 'react-dom';
import { BrowserRouter } from 'react-router-dom';
import { Routes, Route } from 'react-router-dom';

function App() {

 let currentDate = new Date().getTime();
  const filterByPastRide = rides.filter((item) => item.date < currentDate);
  const filterByUpcoimingRide = rides.filter((item) => item.date > currentDate);

    return (

            <BrowserRouter basename="/">
                <Header/>
                <Routes>
                    <Route path="/" element={< NearestRides rides={filterByUpcoimingRide}/>} exact/>
                    <Route path="/upcoming" element={< UpcomingRides rides={filterByUpcoimingRide}/>} exact/>
                    <Route path="/past" element={< PastRides rides={filterByPastRide}/>} exact/>
                </Routes>
            </BrowserRouter>
    );
}

export default App;
