import React from 'react';
import {Container} from 'react-bootstrap';
import map from '../../assets/images/map.jpg'
import classes from './Ride.css'
import {Col, Row, Card} from 'react-bootstrap';

export const Ride = (props) => {

const closest = (station_path, station_code) => {
    return station_path.reduce(function (prev, curr) {
      return Math.abs(curr - station_code) < Math.abs(prev - station_code) ? curr : prev;
    });

  };

    return (
        <Container className="text-white p-4">
            <Row className="bg-black border p-3">
                <Col sm={4}>
                    <img src={map} alt="map"/>
                </Col>
                <Col sm={8}>
                    <div>
                        <p>
                            <span className={classes.line}>Ride Id
                            </span>: {props.ride.id}
                        </p>
                        <p>
                            <span className={classes.line}>Origin Station
                            </span>: {props.ride.origin_station_code}
                        </p>
                        <p>
                            <span className={classes.line}>station_path
                            </span>: {JSON.stringify(props.ride.station_path)}
                        </p>
                        <p>
                            <span className={classes.line}>Date
                            </span>: {`${new Date(props.ride.date)}`}

                        </p>
                        <p>
                            <span className={classes.line}>Distance
                            </span>: {closest(props.ride.station_path, 40) - 40}
                        </p>
                    </div>
                </Col>
            </Row>
        </Container>
    // <div className={classes.ride}> </div>
    );
};

export default Ride;
